import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';
import { Dropdown } from 'react-bootstrap';

const track = {
    name: "",
    album: {
        images: [
            { url: "" }
        ]
    },
    artists: [
        { name: "" }
    ]
}

function WebPlayback(props) {

    const [player, setPlayer] = useState(undefined);
    const [is_paused, setPaused] = useState(false);
    const [is_active, setActive] = useState(false);
    const [current_track, setTrack] = useState(track);
    const [device, setDevice] = useState([]);
    const [isOpen, setIsOpen] = useState(false);
    const [Track, setCurTrack] = useState([]);

    const getDevice = async () => {
        const response = await axios.get('https://api.spotify.com/v1/me/player/devices', {
            headers: {
                Authorization: `Bearer ${props.token}`
            }
        });
        setDevice(response.data.devices);
    }

    const getplayingTrack = async () => {
        const response = await axios.get('https://api.spotify.com/v1/me/player/currently-playing', {
            headers: {
                Authorization: `Bearer ${props.token}`
            }
        });
        setCurTrack(response.data.item);
    }

    const toggle = () => setIsOpen(!isOpen);

    useEffect(() => {

        const script = document.createElement("script");
        script.src = "https://sdk.scdn.co/spotify-player.js";
        script.async = true;

        document.body.appendChild(script);

        window.onSpotifyWebPlaybackSDKReady = () => {

            const player = new window.Spotify.Player({
                name: 'My_extension',
                getOAuthToken: cb => { cb(props.token); },
                volume: 0.3
            });

            setPlayer(player);

            player.connect().then(success => {
                if (success) {
                    console.log('The Web Playback SDK successfully connected to Spotify!');
                }
            });

            player.addListener('ready', ({ device_id }) => {
                console.log('Ready with Device ID', device_id);
            });

            player.addListener('not_ready', ({ device_id }) => {
                console.log('Device ID has gone offline', device_id);
            });

            player.addListener('player_state_changed', ( state => {

                if (!state) {
                    return;
                }

                setTrack(state.track_window.current_track);
                setPaused(state.paused);

                player.getCurrentState().then( state => {
                    (!state)? setActive(false): setActive(true);
                });
            }));

            player.connect();
        };
        getDevice();
        getplayingTrack();
        console.log('Track', Track)
        // if (Track !== undefined) {
        //     track.name = Track.name;
        //     // track.artists.name = Track.artists.name;
        //     // track.album.images.url = Track.album.images.url;
        //     setTrack(track);
        // }
    }, []);

    const renderDevice = () => {
        // return device.map(devices => (
        //     <div key={devices.id}>
        //         { <p>{devices.name}</p>}
        //         { <p>{devices.type}</p>}
        //     </div>
        // ))
        return <div>
            <Dropdown onClick={toggle}>
            <Dropdown.Toggle variant="success" id="dropdown-basic">
                Devices
            </Dropdown.Toggle>
            <Dropdown.Menu open={isOpen}>
                {device.map((device) => (
                <Dropdown.Item clakey={device.id}>
                    {device.name} ({device.type})
                </Dropdown.Item>
                ))}
            </Dropdown.Menu>
            </Dropdown>
      </div>
    }

    // console.log('deviceHEHE', device)
    return (
        <html>
            <header className="App-header">
            <body className='App'>
                <div className="container">
                    <div className="main-wrapper">

                        <img src={Track.album.images[0].url} 
                            className="now-playing__cover" alt="" />
                        <div className="now-playing__side">
                            <div className="now-playing__name">{Track.name}</div>
                            <div className="now-playing__artist">{Track.artists[0].name}</div>
                            <button className="btn-spotify" onClick={() => { player.previousTrack() }} >
                                &lt;&lt;
                            </button>
                            <button className="btn-spotify" onClick={() => { player.togglePlay() }} >
                                { is_paused ? "PLAY" : "PAUSE" }
                            </button>

                            <button className="btn-spotify" onClick={() => { player.nextTrack() }} >
                                &gt;&gt;
                            </button>
                            {/* {renderDevice()} */}
                        </div>
                    </div>
                </div>
            </body>
            </header>
         </html>
    );
}

export default WebPlayback
