            //         {/* <p>{device.name}</p> */}
            //         {/* <p>{device.type}</p> */}
            //     // </div>

            // ))
        } else {
            return <p>No device found</p>
        }
    }

    return (
        <div className='App'>
            <header className='App-header'>
            <h1>Web Playback</h1>
            <button className='btn-spotify'>Play</button>
            {renderDevice()}
            </header>
         </div>
    );
}

export default WebPlayback
