import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';
import PlayCircleSharpIcon from '@mui/icons-material/PlayCircleSharp';

const track = {
    name: "",
    album: {
        images: {
            url: ""
        }
    },
    artists: {
        name: ""
    }
}

function WebPlayback(props) {

    const [is_paused, setPaused] = useState(false);
    const [playbackState, setPlaybackState] = useState([]);

    const PlayState = async () => {
        const response = await axios.get('https://api.spotify.com/v1/me/player', {
            headers: {
                Authorization: `Bearer ${props.token}`
            }
        });
        setPlaybackState(response.data);
        setPaused(response.data.is_playing);
    }

    const pausePlayback = async () => {
        try {
            const response = await axios.put('https://api.spotify.com/v1/me/player/pause', null, {
                headers: {
                    Authorization: `Bearer ${props.token}`
                }
            });
        } catch (error) {
            console.error(error);
        };
    };

    const playButton = async () => {
        try {
            const response = await axios.put('https://api.spotify.com/v1/me/player/play', null, {
                headers: {
                    Authorization: `Bearer ${props.token}`
                }
            });
        } catch (error) {
            console.error(error);
        };
    };

    const previousTrack = async () => {
        try {
            const response = await axios.post('https://api.spotify.com/v1/me/player/previous', null, {
                headers: {
                    Authorization: `Bearer ${props.token}`
                }
            });
        } catch (error) {
            console.error(error);
        };
    };

    const nextTrack = async () => {
        try {
            const response = await axios.post('https://api.spotify.com/v1/me/player/next', null, {
                headers: {
                    Authorization: `Bearer ${props.token}`
                }
            });
        } catch (error) {
            console.error(error);
        };
    };

    const renderStatus = () => {
        if (is_paused) {
            return (
            <PlayCircleSharpIcon className="btn-spotify" onClick={() => { is_paused ? pausePlayback() : playButton() }} >
            </PlayCircleSharpIcon>
            );
        } else {
            return (
                <button className="btn-spotify" onClick={() => { is_paused ? pausePlayback() : playButton() }} >
                    PLAY
                </button>
            );
        }
    }

    useEffect(() => {
        const refresh = setInterval(() => {
            PlayState();
        }, 1000);
        return () => clearInterval(refresh);
    }, []);

    return (
        <>
            <body className='App'>
                <div className="container">
                    <div className="main-wrapper">
                        <img src={playbackState.item ? playbackState.item.album.images[0].url : ""} 
                            className="now-playing__cover" alt="" />

                        <div className="now-playing__side">
                            <div className="now-playing__name">{playbackState.item ? playbackState.item.name : ""}</div>
                            <div className="now-playing__artist">{playbackState.item ? playbackState.item.artists[0].name : ""}</div>

                            <button className="btn-spotify" onClick={() => { previousTrack() }} >
                                &lt;&lt;
                            </button>
                            {/* {/* <PlayCircleSharpIcon className="btn-spotify" onClick={() => { is_paused ? pausePlayback() : playButton() }} >
                                { is_paused ? "PAUSE" : "PLAY"}
                            </PlayCircleSharpIcon> */}
                            {renderStatus()} */}
                            <button className="btn-spotify" onClick={() => { nextTrack() }} >
                                &gt;&gt;
                            </button>
                        </div>
                    </div>
                </div>
            </body>
        </>
    );
}

export default WebPlayback
